import React from 'react';
import Layout from '../components/Layout';
import Link from 'next/Link';

export default function pokemon({ poke }) {
    return (
        <Layout title={poke.name}>
            <h1 className="text-4xl mb-2 text-center capitalize">
                {poke.id}. {poke.name}
            </h1>
            <img className="mx-auto" src={poke.image} alt={poke.name} />
            <p>
                <span className="font-bold mr-2">Weight:</span> {poke.weight}
            </p>
            <p>
                <span className="font-bold mr-2">Height:</span>
                {poke.height}
            </p>
            <h2 className="text-2xl mt-6 mb-2">Types</h2>
            {poke.types.map((type, index) => (
                <p key="index">{type.type.name}</p>
            ))}
            <p className="mt-10 text-center">
                <Link href="/">
                    <a className="text-2xl underline">Home</a>
                </Link>
            </p>
        </Layout>
    );
}

export async function getServerSideProps({ query }) {
    const id = query.id;
    try {
        const res = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
        const poke = await res.json();
        const paddedId = ('00' + id).slice(-3);
        poke.image = `https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${paddedId}.png`;
        return {
            props: { poke },
        };
    } catch (err) {
        console.error(err);
    }
}
