import React, { useState } from 'react'
import Link from 'next/Link';
import Layout from '../components/Layout';
export default function Home({ pokemon }) {
    const [amount, setAmount] = useState([])
    return (
        <Layout title="NextJS Pokedex">
            <h1 id='title' className="text-5xl mb-8 text-center">Poke NFT</h1>
            <ul>
                {pokemon.map((pokeman, index) => (
                    console.log("poke data:", pokeman),
                    <li key={index}>
                        <Link href={`/pokemon?id=${index + 1}`}>
                            <a className="border p-4 border-grey my-2 hover:shadow-md capitalize flex items-center text-lg bg-gray-200 rounded-md">
                                <img
                                    src={pokeman.image}
                                    alt={pokeman.name}
                                    className="w-20 h-20 mr-3"
                                />
                                <span className="mr-2 font-bold">
                                    {index + 1}.
                                </span>
                                {pokeman.name}
                                <span>
                                    <button onClick={() => setAmount([...amount[index]-=1])}className='bg-blue-500 hover:bg-blue-700 text-white font-bold mx-20 py-2 px-4 rounded'>
                                        -
                                    </button>
                                    <h4>{amount.length && amount[index]}</h4>
                                    <button onClick={() => setAmount([...amount[index]-=1])} className='bg-blue-500 hover:bg-blue-700 text-white font-bold mx-20 py-2 px-4 rounded'>
                                        +
                                    </button>
                                    <button className='bg-blue-500 hover:bg-blue-700 text-white font-bold mx-20 py-2 px-4 rounded'>
                                        Buy
                                    </button>
                                </span>
                            </a>
                        </Link>
                    </li>
                ))}
            </ul>
        </Layout>
    );
}

export async function getStaticProps(context) {
    try {
        const res = await fetch('https://pokeapi.co/api/v2/pokemon?limit=150');
        const { results } = await res.json();
        const pokemon = results.map((pokeman, index) => {
            const paddedId = ('00' + (index + 1)).slice(-3);

            const image = `https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${paddedId}.png`;
            return { ...pokeman, image };
        });
        return {
            props: { pokemon },
        };
    } catch (err) {
        console.error(err);
    }
}
